import React, { Component } from 'react';
import '../styles/blackjack.css';
import * as Constants from '../js/constants';

class DealerHand extends Component {
    constructor(props) {
        super(props);
        this.state = {
            dHand: []
        }
    }

    componentDidUpdate(prevProps) {
        if (prevProps.dHand.length !== this.props.dHand.length){
            let lastIndex = this.props.dHand.length - 1;
            this.setState({dHand: this.state.dHand.push(this.props.dHand[lastIndex])});
        }
    }

    render() {
        return(
            <div className="dealerHand">
                Hello There welcome to the game
            </div>
        )
    }
}

export default DealerHand
