import React, { Component } from 'react';
import '../styles/blackjack.css';

class StartScreen extends Component {
    constructor(props) {
        super(props);
        this.state = {
            buyin: 100,
            decks: 1
        }
        this.handleChange = this.handleChange.bind(this);
    }

    generateDeckOptions() {
        let options = [];
        for(let i = 1; i <= 6; i++) {
            options.push(
                <options value={i}>i</options>
            );
        }
        return options;
    }

    handleChange(e) {
        e.preventDefault();
        console.log(e.target.name);
        console.log(e.target.value);
        this.setState({[e.target.name]: e.target.value}); 
    }


    playGame() {

    }

    render() {
        let mStyle = {color: 'black', fontSize: '48px'};
        let message = 'Blackjack';
        if (!this.state.buyin > 0) {
            mStyle = {color: 'red', fontSize: '48px'};
            message = 'You must select a buy in';
        }

        return(
            <div className="appBackground">
                <div className="start">
                    <p style={mStyle}>{message}</p>
                    <label>Select a number of decks: </label>
                    <br />
                    <select onchange={this.handleChange} name="decks" value={this.state.decks}>
                        {this.generateDeckOptions()}
                    </select>
                    <br />
                    <label>How much would you like to buy in? </label>
                    <input value={this.state.buyin} name="buyin" type="number" onchange={this.handleChange}></input>
                    <br />
                    <button onclick={this.playGame}>Let's Play!</button>
                </div>
            </div>
        )
    }
}

export default StartScreen
