import React, { Component } from 'react';
import '../styles/blackjack.css';
import Table from './Table';
import StartScreen from './StartScreen';
class Main extends Component {
    constructor(props) {
        super(props);
        this.state = {
            bankRoll: 0,
            decks: 1,
            letsPlay: false
        }
    }


    render() {
        if (!this.state.letsPlay) {
            return(
                <StartScreen />
            )
        } else {
            return(
                <Table />
            )
        }
    }
}

export default Main
