import React, { Component } from 'react';
import '../styles/blackjack.css';
import * as Constants from '../js/constants';

class PlayerHand extends Component {
    constructor(props) {
        super(props);
        this.state = {
            pHand: []
        }
    }

    componentDidUpdate(prevProps) {
        if (prevProps.pHand.length !== this.props.pHand.length){
            let lastIndex = this.props.pHand.length - 1;
            this.setState(prevState => ({
                pHand: [...prevState.pHand, this.props.pHand[lastIndex]]
            }));
            //this.setState({pHand: this.state.pHand.push(this.props.pHand[lastIndex])});
        }
    }

    buildHand() {
        //for(let i = 0; i < this.state.pHand)
    }

    render() {
        let cards = this.buildHand();
        return(
            <div className="playerHand">
                { cards } 
            </div>
        )
    }
}

export default PlayerHand
