import React, { Component } from 'react';
import '../styles/blackjack.css';
import * as Constants from '../js/constants';

class PlayerHand extends Component {
    constructor(props) {
        super(props);
        this.state = {
            pHand: []
        }
    }

    componentDidUpdate(prevProps) {
        if (prevProps.pHand.length !== this.props.pHand.length){
            this.setState({pHand: this.props.pHand});
        }
    }

    buildHand() {
        let cards = [];
        for(let i = 0; i < this.state.pHand.length; i++) {
            cards.push(
                <img src={require(Constants.path+this.state.pHand[i].image)}></img>
            )
        }
        return cards;
    }

    render() {
        let cards = this.buildHand();
        return(
            <div className="playerHand">
                { cards } 
            </div>
        )
    }
}

export default PlayerHand
