import React, { Component } from 'react';
import '../styles/blackjack.css';

class StartScreen extends Component {
    constructor(props) {
        super(props);
        this.state = {
            buyin: 100
        }
        this.updateBuyIn = this.updateBuyIn.bind(this);
    }

    generateDeckOptions() {
        let options = [];
        for(let i = 1; i <= 6; i++) {
            options.push(
                <options value={i}>i</options>
            );
        }
        return options;
    }

    updateBuyIn(e) {
        e.preventDefault();
        this.setState({[e.target.name]: e.target.value}); 
    }

    render() {
        return(
            <div className="appBackground">
                <div className="start">
                    <label>Select a number of decks: </label>
                    <select>
                        {this.generateDeckOptions()}
                    </select>
                    <label>How much would you like to buy in? </label>
                    <input value={this.state.buyin} name="buyin" type="number" onchange={this.updateBuyIn}></input>
                </div>
            </div>
        )
    }
}

export default StartScreen
